const t=document.querySelector(".nav-toggle"),n=document.querySelector(".nav-menu");t.addEventListener("click",function(){n.classList.toggle("hidden")});const c=document.querySelector(".theme-toggle"),e=document.documentElement.classList;c.addEventListener("click",function(){e.contains("dark")?(localStorage.setItem("theme","light"),e.remove("dark")):(localStorage.setItem("theme","dark"),e.add("dark"))});
